mapper.select:
SELECT g.`IDS`,g.`VENDORIDS`,g.`TITLE`,g.`PRICE`,g.`GOODS_CLASS_IDS`,g.`DIS_PRICE`,g.`COMMON`,g.`STANDARD`,g.`CONTENT`,g.`STORE_NUM`, g.`REVIEW_DESC`,g.`REVIEW_STATUES`,g.`STATUS`,g.`START_TIME`,g.`END_TIME`,f.`FILEPATH` FROM chn_goodsinfo g,goods_file f WHERE 1=1 AND g.`IDS`=f.`GOODSIDS`AND g.`STATUS`="1" AND g.`REVIEW_STATUES`="1"
AND g.TITLE like CONCAT('%','${title}','%')
AND g.GOODS_CLASS_IDS = #{goodsClassIds,jdbcType=VARCHAR}
