mapper.select:
	select cgc.*, cg.`NAME`, smu.VENDOR_SNM
	from chn_goodsinfo_check cgc, chn_goods_class cg, sp_merchant_user smu
	
		cgc.GOODS_CLASS_IDS = cg.IDS and cgc.VENDORIDS = smu.VENDOR_ID
		
			and GOODSIDS like #{goodsids}
		
		
			and TITLE like #{title}
		
		
			and START_TIME >= #{startTime} and END_TIME <= #{endTime}
		
		
			and VENDORIDS like #{vendorids}
		
		
			and GOODS_CLASS_IDS like #{goodsClassIds}
		
		
			and TYPE = #{type}
		
		
			and REVIEW_STATUES = #{reviewStatues}
