mapper.select:
SELECT childorder.*, asales.TYPE AS after_type,asales.APPROVE_STATUS,mainorder.PAYWAY FROM (SELECT orderinfo.*, gc.name FROM ( SELECT co.*,gf.TITLE,gf.STANDARD,gf.CODE,gf.GOODS_CLASS_IDS,gf.TYPE FROM py_child_goodsorder co,chn_goodsinfo gf WHERE co.goodsids = gf.ids
and co.transactionid like concat(concat('%', #{transactionid}), '%')
and co.mainorder_ids like concat(concat('%', #{mainorderIds}), '%')
and co.ORDER_TYPE = #{orderType}
and gf.type = #{type} ) orderinfo,chn_goods_class gc WHERE orderinfo.goods_class_ids = gc.IDS) childorder LEFT JOIN sp_after_sales asales ON childorder.TRANSACTIONID = asales.ORDER_IDS LEFT JOIN py_main_goodsorder mainorder ON childorder.MAINORDER_IDS = mainorder.TRANSACTIONID order by ${sort} ${order}

mapper.update:
update chn_goodsinfo
	set STORE_NUM=#{storeNum,jdbcType=DECIMAL}
where IDS = #{goodsId,jdbcType=VARCHAR}

update chn_goodsinfo
	set STORE_NUM = STORE_NUM + #{goodsNum,jdbcType=DECIMAL} 
where IDS = #{goodsids,jdbcType=VARCHAR}
