mapper.select:
  	SELECT mainorder.*, childorder.ORDER_TYPE, afterorder.TYPE, muser.VENDOR_SNM
	FROM py_main_goodsorder mainorder 
		LEFT JOIN py_child_goodsorder childorder 
		ON mainorder.TRANSACTIONID = childorder.MAINORDER_IDS
		LEFT JOIN sp_after_sales afterorder
		ON childorder.TRANSACTIONID = afterorder.ORDER_IDS
		LEFT JOIN sp_merchant_user muser
		ON mainorder.VENDOR_IDS = muser.VENDOR_ID
	
		
			and mainorder.transactionid like concat(concat('%', #{transactionid}), '%')
		
		
			and mainorder.BIGORDER_ID like concat(concat('%', #{bigorderId}), '%')
		
		
			and mainorder.PAY_STATUS = #{payStatus}
		 
		
			and mainorder.CONT_NAME like concat(concat('%', #{contName}), '%') 
		
		
			and mainorder.PAYWAY = #{payway}
		 
		
			and mainorder.STATUS = #{orderStatus}
		
		
			and mainorder.ORDER_TIME >= #{minPayTime}
		
		
			and mainorder.ORDER_TIME <= #{maxPayTime}
		
		
			and mainorder.SETTLE_TIME like concat(concat('%', #{settleTimeFee}), '%')
		
		
			and mainorder.VENDOR_IDS = #{vendorIds}
		
	
	order by ${sort} ${order}
